export default {
  props: {
    size: {
      default: 'md',
      type: String
    },
    color: {
      default: 'gazblue',
      type: String
    },
    type: {
      default: 'button',
      type: String
    }
  },
  computed: {
    colorClass() {
      return `tw-bg-${this.color}`;
    },

    paddingClass() {
      if (this.size === 'md') return 'tw-px-4 tw-py-3';else return 'tw-px-2 tw-py-1';
    },

    textClass() {
      if (this.size === 'md') return 'tw-text-base';else return 'tw-text-sm';
    },

    classes() {
      return 'app-btn tw-bg-[#0079c2]' + ' ' + this.colorClass + ' ' + this.paddingClass + ' ' + this.textClass;
    }

  }
};
